import { useState } from "react";

import "./ExpenseForm.css";

const ExpenseFormPrac = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [isClick, setIsClick] = useState(false);

  const isClicked = () => {
    if (isClick == true) {
      setIsClick(false);
    } else if (isClick == false) {
      setIsClick(true);
    }
  };
  // const [userInput, setUserInput] = useState({
  //     enteredTitle: '',
  //     enteredAmount: '',
  //     enteredDate: '',
  // });
  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    // setUserInput((prevState) => {
    //     return {...prevState, enteredTitle: event.target.value};
    // })

    // setUserInput({
    //     ...userInput,
    //     enteredTitle: event.target.value,
    // });
  };
  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredAmount: event.target.value,
    // });
  };
  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredDate: event.target.value,
    // });
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
    props.onSaveExpenseData(expenseData);
  };

  let formCom = (
    <div className="new=expense__actions">
      <button type="submit" onClick={isClicked}>Add Expense</button>
    </div>
  );

  if (isClick == false) {
    formCom = (
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              value={enteredTitle}
              onChange={titleChangeHandler}
            ></input>
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              type="number"
              value={enteredAmount}
              onChange={amountChangeHandler}
              min="0.01"
              step="0,01"
            ></input>
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              value={enteredDate}
              onChange={dateChangeHandler}
              min="2019-01-01"
              max="2022-12-31"
            ></input>
          </div>
        </div>
        <div className="new=expense__actions">
          <button onClick={isClicked}>Cancel</button>
          <button onClick={isClicked} type="submit">
            Add Expense
          </button>
        </div>
      </form>
    );
  }

  return <div>{formCom}</div>;
};

export default ExpenseFormPrac;
